import {Component} from 'react'
import { Row, Button } from 'react-bootstrap'
import PhonesService from './../../../services/phones.service'
import PhoneCard from './PhoneCard'
import {Link} from 'react-router-dom'

class PhonesList extends Component{

    constructor(){
        super()
        this.state = {
            phones: undefined
        }
        this.phonesService = new PhonesService()
    }

    componentDidMount = () =>{
        this.phonesService
            .getPhones()
            .then( res => this.setState({phones: res.data}))
    }


    render(){

        return (

            this.state.phones===undefined
            ?
            <h1>The list goes here...</h1>
            :
            <>
            <Link to = {`/`} >
                    <Button variant="primary">Index Page</Button>
            </Link>

            <Row>
                {this.state.phones.map(phone =>  <PhoneCard {...phone}/>)}
            </Row>
            </>



        )
    }

}

export default PhonesList
