import {Component} from 'react'
import { Row, Col } from 'react-bootstrap'
import PhonesService from './../../../services/phones.service'
import PhoneCard from './PhoneCard'

class PhonesList extends Component{

    constructor(){
        super()
        this.state = {
            phones: undefined
        }
        this.phonesService = new PhonesService()
    }

    componentDidMount = () =>{
        this.phonesService
            .getPhones()
            .then( res => this.setState({phones: res.data}))
    }


    render(){

        return (

            this.state.phones===undefined
            ?
            <h1>The list goes here...</h1>
            :
            <Row>
                {this.state.phones.map(phone =>  <PhoneCard phone={phone}/>)}
            </Row>




        )
    }

}

export default PhonesList
