import { Container, Button, Modal } from 'react-bootstrap'
import PhonesList from './PhonesList'
import {Link} from 'react-router-dom'
import {Component} from 'react'
import PhoneForm from './PhoneForm'


class PhonesPage extends Component{

    constructor(){
        super()
        this.state = {
            modal: false,
        }
    }

    closeNewPhoneModal = () => {
        this.setState({
            modal: false
        })
    }

    openNewPhoneModal = () => {
        this.setState({
            modal: true
        })
    }

    componentDidUpdate = (prevProps, prevState) => prevState.modal !== this.state.modal && this.props?.history?.push('/phones')


render(){    
    return(
        <Container>

            <Link to = {`/`} >
                    <Button variant="primary">Index Page</Button>
            </Link>
            <Link className="btn btn-success" to = {`/phones/new`} >New Phone</Link>
            <Button variant="secondary" onClick={this.openNewPhoneModal}>
                New Phone Modal
            </Button>

            <PhonesList re/>


            <Modal show={this.state.modal} onHide={this.closeNewPhoneModal}>
                <Modal.Header closeButton>
                <Modal.Title>New Phone</Modal.Title>
                </Modal.Header>

                <Modal.Body>

                    <PhoneForm closeNewPhoneModal={this.closeNewPhoneModal}/>

                </Modal.Body>

                <Modal.Footer>
                <Button variant="secondary" onClick={this.closeNewPhoneModal}>
                    Close
                </Button>
                <Button variant="primary" onClick={this.closeNewPhoneModal}>
                    Save Changes
                </Button>
                </Modal.Footer>

            </Modal>


        </Container>

    )
}
}

export default PhonesPage
